head.script:
$(document).ready(function() {
  //Default Action
  $(".tab_content").hide(); //Hide all content
  $("ul.tabs li:first").addClass("active").show(); //Activate first tab
  $(".tab_content:first").show(); //Show first tab content
  //On Click Event
  $("ul.tabs li").click(function() {
    $("ul.tabs li").removeClass("active"); //Remove any "active" class
    $(this).addClass("active"); //Add "active" class to selected tab
    $(".tab_content").hide(); //Hide all tab content
    var activeTab = $(this).find("a").attr("href"); //Find the rel attribute value to identify the active tab + content
    $(activeTab).fadeIn(); //Fade in the active content
    return false;
  });
});

b:if.script:
//
//Script by Aneesh of www.bloggerplugins.org
//Released on August 19th August 2009
var relatedTitles = new Array();
var relatedTitlesNum = 0;
var relatedUrls = new Array();
var thumburl = new Array();
function related_results_labels_thumbs(json) {
  for (var i = 0; i < json.feed.entry.length; i++) {
    var entry = json.feed.entry[i];
    relatedTitles[relatedTitlesNum] = entry.title.$t;
    try
    {thumburl[relatedTitlesNum]=entry.media$thumbnail.url;}
    catch (error){
      s=entry.content.$t;a=s.indexOf("<img");b=s.indexOf("src=\"",a);c=s.indexOf("\"",b+5);d=s.substr(b+5,c-b-5);if((a!=-1)&&(b!=-1)&&(c!=-1)&&(d!="")){
        thumburl[relatedTitlesNum]=d;} else thumburl[relatedTitlesNum]='http://lh4.ggpht.com/_NNS6r_z4aeg/SuOQIOq8y3I/AAAAAAAAN_o/hD-YGKj_QFg/sinimagen.jpg';
    }
    if(relatedTitles[relatedTitlesNum].length>35) relatedTitles[relatedTitlesNum]=relatedTitles[relatedTitlesNum].substring(0, 35)+"...";
    for (var k = 0; k < entry.link.length; k++) {
      if (entry.link[k].rel == 'alternate') {
        relatedUrls[relatedTitlesNum] = entry.link[k].href;
        relatedTitlesNum++;
      }
    }
  }
}
function removeRelatedDuplicates_thumbs() {
  var tmp = new Array(0);
  var tmp2 = new Array(0);
  var tmp3 = new Array(0);
  for(var i = 0; i < relatedUrls.length; i++) {
    if(!contains_thumbs(tmp, relatedUrls[i]))
    {
      tmp.length += 1;
      tmp[tmp.length - 1] = relatedUrls[i];
      tmp2.length += 1;
      tmp3.length += 1;
      tmp2[tmp2.length - 1] = relatedTitles[i];
      tmp3[tmp3.length - 1] = thumburl[i];
    }
  }
  relatedTitles = tmp2;
  relatedUrls = tmp;
  thumburl=tmp3;
}
function contains_thumbs(a, e) {
  for(var j = 0; j < a.length; j++) if (a[j]==e) return true;
  return false;
}
function printRelatedLabels_thumbs() {
  for(var i = 0; i < relatedUrls.length; i++)
  {
    if((relatedUrls[i]==currentposturl)||(!(relatedTitles[i])))
    {
      relatedUrls.splice(i,1);
      relatedTitles.splice(i,1);
      thumburl.splice(i,1);
    }
  }
  var r = Math.floor((relatedTitles.length - 1) * Math.random());
  var i = 0;
  if(relatedTitles.length>0) document.write('<div class="title us">'+relatedpoststitle+'</div>');
  document.write('<div style="clear: both;"/>');
  while (i < relatedTitles.length && i < 20 && i<maxresults) {

    document.write('<section id="related"><article class="rel" style="float:left;"><a href="' + relatedUrls[r] + '"><header>' +relatedTitles[r]+ '</header><figure><img width="116" height="164" src="' +thumburl[r]+ '"></figure><div class="mask"></div></a></article></section>');
    if (r < relatedTitles.length - 1) {
      r++;
    } else {
      r = 0;
    }
    i++;
  }
  document.write('</div>');
}
