head.script:
 // Todo comienza cuando la página ya fue cargada
$('document').ready(function(){
         // este es el div "ventana"  outer, lo obtenemos usando jquery para luego hacer
         // scroll de su contenido
         var outer= $('.generos') ;
         var preview_pos;
         // este el el contenido interior lo necesitamos obtener para identificar su tamaño
         // en este caso son 500px , pero podria cambiar si lo hacemos mas grande y para
         // hacerlo un poco mas automático simplemente lo tomamos de la fuente en lugar
         // de hardcodearlo
         var preview_els = $(".swSlider div");
         var div_width   = preview_els.eq(0).width();

         // agregamos a los links del paginador un evento mouseover
         $('.swControls a').bind('click', function(event){
                  //obtenemos el elemento sobre el que se paso el mouse
                 var link = event.currentTarget;
                 // obtenemos el número de elemento con el atributo page
                 // que habiamos definido previamente, esto podria ser incluso
                 // automatico y nos evitariamos agregar este atributo extraño
                 // pero para simplificar decidi hacerlo asi , además anda...
                 var pos= $(link).attr('page');
                 //calculamos que posición debería aparecer en la ventana
                 pos=(pos-1)*div_width;
                 // hacemos scroll hasta la posicion, con animate para que se vea
                 // el movimiento, el metodo stop() permite que si cambiamos de página
                 // la animación no siga para la página anterior y reaccione  rápido al cambio
                 outer.stop().animate( {'scrollLeft' : pos },500 );

                  // aca calculamos la posición del link para poner el marcador abajo
                 var left=$(link).position().left;
                 // un ligero ajuste
                 left=left+14;
                 // y lo animamos para que se mueva a la posición definida
                 $("#arrow").stop().animate( {'left' : left },500 );
         })

          // cuando el usuario refresca la pagina con F5 o recien entra es necesario
          // resetear la pagina a la posicion inicial , suponiendo que la pagina en la
         // que estamos es la inicial.
         outer.animate( {'scrollLeft' : 0}, 0 );
         $(".swSlider").css('width', preview_els.length * div_width);
         $("#arrow").css('left', 294);
})

