/****************************
 Z-Kreations - simple Slider V1
****************************/

$(document).ready(function(){
    //Especificamos el contenido de nuestro slider
    var slider, tiempo;
        slider = $('#slider .slider-contenido > .slide');
    //Direcion desde donde se desplasa el slider
    function slideranimacion(direccion){
        position = $('#slider').scrollLeft();
        totalWidth = (slider.length * slider[0].offsetWidth) - slider[0].offsetWidth
    //La funcion de arriba comprueba este valor para saber la direccion
    switch (direccion) {
        case 'right':

    if (position+slider[0].offsetWidth > totalWidth){ //Al terminar volver al principio
        $('#slider:not(:animated)').animate({scrollLeft:0},1000);
        } else {
        $('#slider:not(:animated)').animate({scrollLeft:position+slider[0].offsetWidth},1000);
    }

    break;
        case 'left':

    if (position-slider[0].offsetWidth < 0){//Al terminar volver al principio
        $('#slider:not(:animated)').animate({scrollLeft:totalWidth},1000);
        } else {
        $('#slider:not(:animated)').animate({scrollLeft:position-slider[0].offsetWidth},1000);
    }
    break;}}
    //Animacion automatica del slider establecida cada 5 segundos
    //Editar este valor para cambiar la duracion de la animacion automatica (5000 = 5 segundos)
   function inittiempo(){
        tiempo = setInterval(function(){slideranimacion('right');}, 5000);
   }

    $('#slider .slider-contenido').css('width',slider[0].offsetWidth * slider.length);

    //Boton adelante
    $('.siguiente').click(function(){
        clearInterval(tiempo);
        slideranimacion('right')
        inittiempo();
        return false;
    });

    //Boton atras
    $('.anterior').click(function(){
        clearInterval(tiempo);
        slideranimacion('left');
        inittiempo();
        return false;
    });
    inittiempo();
});
