summ = summary_txt;
}
if(img.length>=1) {
imgtag = '<span class="p-img"><a class="img-content" href="'+ pURL +'"><img src="'+img[0].src+'" width="'+img_thumb_width+'px"/></a></span>';
summ = summary_txt;
}
var summary = imgtag + '<div>' + removeHtmlTag(div.innerHTML,summ) + '</div>';
